import React, { useState, useRef } from "react";
import leftArrowIcon from "../left-arrow.png"; // Replace with your actual path
import rightArrowIcon from "../right-arrow.png"; // Replace with your actual path
import "../components/Component.css";

const Component = ({ images }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [scrollIndex, setScrollIndex] = useState(0);
  const imageWidth = 300;
  const containerRef = useRef(null);

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const handlePrevClick = () => {
    setScrollIndex(Math.max(scrollIndex - 1, 0));
  };

  const handleNextClick = () => {
    setScrollIndex(Math.min(scrollIndex + 1, images.length - 1));
  };

  // Calculate the scroll position based on scrollIndex
  const scrollPosition = scrollIndex * imageWidth;

  return (
    <div className="image-gallery">
      <div className="arrow-buttons">
        <button
          className="arrow-button left"
          onClick={handlePrevClick}
          disabled={scrollIndex === 0}
        >
          <img src={leftArrowIcon} alt="Left Arrow" />
        </button>
      </div>
      <div className="scroll-container" ref={containerRef}>
        <div
          className="image-container"
          style={{ transform: `translateX(-${scrollPosition}px)` }}
        >
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index}`}
              className="circleImages"
              onClick={() => handleImageClick(image)}
            />
          ))}
        </div>
      </div>
      <div className="arrow-buttons">
        <button
          className="arrow-button right"
          onClick={handleNextClick}
          disabled={scrollIndex === images.length - 1}
        >
          <img src={rightArrowIcon} alt="Right Arrow" />
        </button>
      </div>
      {selectedImage && (
        <div className="modal">
          <span className="close-button" onClick={handleCloseModal}>
            &times;
          </span>
          <img
            src={selectedImage}
            alt="Full Screen"
            className="full-screen-image"
          />
        </div>
      )}
    </div>
  );
};

export default Component;
