import React, { useState } from "react";
import "../components/Component.css";

const Component = ({ images }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const handleScroll = (direction) => {
    const container = document.getElementById("image-gallery-container");
    const step = 200; // Adjust the step as needed

    if (direction === "left") {
      setScrollPosition(scrollPosition - step);
    } else if (direction === "right") {
      setScrollPosition(scrollPosition + step);
    }

    container.scrollLeft = scrollPosition;
  };

  return (
    <div className="image-gallery">
      <button className="scroll-button" onClick={() => handleScroll("left")}>
        &lt;
      </button>
      <div id="image-gallery-container" className="image-gallery-container">
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Image ${index}`}
            className="circleImages"
            onClick={() => handleImageClick(image)}
          />
        ))}
      </div>
      <button className="scroll-button" onClick={() => handleScroll("right")}>
        &gt;
      </button>
      {selectedImage && (
        <div className="modal">
          <span className="close-button" onClick={handleCloseModal}>
            &times;
          </span>
          <img
            src={selectedImage}
            alt="Full Screen"
            className="full-screen-image"
          />
        </div>
      )}
    </div>
  );
};

export default Component;
