import React, { useState } from "react";
import "../components/Component.css";

const Component = ({ images }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const imagesPerRow = 3;

  const handlePrevClick = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleNextClick = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="image-gallery">
      <div className="image-row">
        {/* Displaying three images in a row */}
        {images.slice(currentImageIndex, currentImageIndex + imagesPerRow).map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Image ${index}`}
            className="carousel-image"
          />
        ))}
      </div>
      <div className="carousel-controls">
        <button className="carousel-button left" onClick={handlePrevClick}>
          &lt;
        </button>
        <button className="carousel-button right" onClick={handleNextClick}>
          &gt;
        </button>
      </div>
    </div>
  );
};

export default Component;
