import React, { useState } from "react";
import "../components/Component.css";

const Component = ({ images }) => {
  const [startIndex, setStartIndex] = useState(0);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const handleNextImages = () => {
    setStartIndex((prevIndex) => Math.min(prevIndex + 3, images.length - 1));
  };

  const handlePrevImages = () => {
    setStartIndex((prevIndex) => Math.max(prevIndex - 3, 0));
  };

  const visibleImages = images.slice(startIndex, startIndex + 3);

  return (
    <div className="image-gallery">
      <div className="image-scroll-container">
        <button
          className="scroll-button prev-button"
          onClick={handlePrevImages}
          disabled={startIndex === 0} // Disable when at the beginning
        >
          &lt;
        </button>
        <div className="image-row">
          {visibleImages.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index}`}
              className="circleImages"
              onClick={() => handleImageClick(image)}
            />
          ))}
        </div>
        <button
          className="scroll-button next-button"
          onClick={handleNextImages}
          disabled={startIndex + 3 >= images.length} // Disable when at the end
        >
          &gt;
        </button>
      </div>
      {selectedImage && (
        <div className="modal">
          <span className="close-button" onClick={handleCloseModal}>
            &times;
          </span>
          <img
            src={selectedImage}
            alt="Full Screen"
            className="full-screen-image"
          />
        </div>
      )}
    </div>
  );
};

export default Component;
