import React, { useState } from "react";
import "../components/Component.css";

const Component = ({ images }) => {
  const [scrollIndex, setScrollIndex] = useState(0);
  const imagesPerRow = 3;

  const handlePrevClick = () => {
    setScrollIndex(Math.max(scrollIndex - 1, 0));
  };

  const handleNextClick = () => {
    setScrollIndex(Math.min(scrollIndex + 1, images.length - imagesPerRow));
  };

  return (
    <div className="image-gallery">
      <button className="carousel-button left" onClick={handlePrevClick}>
        &lt;
      </button>
      <div className="scroll-container">
        {/* Displaying a row of images */}
        <div
          className="image-row"
          style={{
            transform: `translateX(-${scrollIndex * 100}%)`,
            transition: "transform 0.3s ease",
          }}
        >
          {images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Image ${index}`}
              className="carousel-image"
            />
          ))}
        </div>
      </div>
      <button className="carousel-button right" onClick={handleNextClick}>
        &gt;
      </button>
    </div>
  );
};

export default Component;
